sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var whatNeeded = g_form.getValue('u_what_needed');

   // Clear all choices from the What Needed field choice list.

   g_form.clearOptions('u_what_needed');

   // If the Request Type is HR, display the HR choices

   if(newValue == 'hr') { // newValue refers to the field specified in the client scripts 'Field name' field
     g_form.addOption('u_what_needed', 'h1', 'Human Resources 1');
     g_form.addOption('u_what_needed', 'h2', 'Human Resources 2');
     g_form.addOption('u_what_needed', 'other', 'Other');
   }

   // If the Request Type is Facilities, display the Facilities choices

   if(newValue == 'facilities') {
     g_form.addOption('u_what_needed', 'f1', 'Facilities 1');
     g_form.addOption('u_what_needed', 'f2', 'Facilities 2');
     g_form.addOption('u_what_needed', 'other', 'Other');
   }

   // If the Request Type is Legal, display the Legal choices

   if(newValue == 'legal') {
     g_form.addOption('u_what_needed', 'l1', 'Legal 1');
     g_form.addOption('u_what_needed', 'l2', 'Legal 2');
     g_form.addOption('u_what_needed', 'other', 'Other');
   }

   /* If the form is loading and it is not a new record, set the What needed
      value to the value from the record that was loaded.*/

  if(isLoading && !g_form.isNewRecord()) {
    g_form.setValue('u_what_needed', whatNeeded);
  }    
}
